import React, { useState } from "react";
import "./Dropdown.css"; // Assure-toi d'avoir un fichier Dropdown.css pour styliser ton dropdown si nécessaire
const Dropdown = ({ options, onSelect }) => {
    const [selectedOption, setSelectedOption] = useState(null);
    const [isOpen, setIsOpen] = useState(false);
    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };
    const handleSelectOption = (optionKey) => {
        const selected = options[optionKey];
        setSelectedOption(selected);
        onSelect(selected); // Appelle la fonction onSelect passée en props avec l'option sélectionnée
        setIsOpen(false); // Ferme le dropdown après sélection
    };
    return (React.createElement("div", { className: "dropdown-container" },
        React.createElement("div", { className: "dropdown-header", onClick: toggleDropdown },
            selectedOption ? selectedOption.label : "Select an option",
            React.createElement("i", { className: `dropdown-icon ${isOpen ? "open" : ""}` })),
        isOpen && (React.createElement("ul", { className: "dropdown-list" }, Object.keys(options).map((optionKey) => (React.createElement("li", { key: optionKey, className: "dropdown-item", onClick: () => handleSelectOption(optionKey) }, options[optionKey].label)))))));
};
export default Dropdown;
